mapper.update:
update admin set
        name = #{name}
               ,
        username = #{username}
               ,
        password = #{password}
               ,
        mobile = #{mobile}
               ,
        email = #{email}
               ,
        role_id = #{roleId}
               ,
        parent_id = #{parentId}
               ,
        parent_ids = #{parentIds}
               ,
        locked = #{locked}
               ,
        created_at = #{createdAt}
               ,
        updated_at = #{updatedAt}
  where id = #{id}

mapper.select:
select * from admin where 1=1
        name = #{name}

            AND name LIKE #{name}

        username = #{username}

            AND username LIKE #{username}

        password = #{password}

            AND password LIKE #{password}

        mobile = #{mobile}

            AND mobile LIKE #{mobile}

        email = #{email}

            AND email LIKE #{email}

        role_id = #{roleId}

            AND role_id LIKE #{roleId}

        parent_id = #{parentId}

            AND parent_id LIKE #{parentId}

        parent_ids = #{parentIds}

            AND parent_ids LIKE #{parentIds}

        locked = #{locked}

            AND locked LIKE #{locked}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}

limit #{pageNo}, #{pageSize}

select count(id) from admin where 1=1
        name = #{name}

            AND name LIKE #{name}

        username = #{username}

            AND username LIKE #{username}

        password = #{password}

            AND password LIKE #{password}

        mobile = #{mobile}

            AND mobile LIKE #{mobile}

        email = #{email}

            AND email LIKE #{email}

        role_id = #{roleId}

            AND role_id LIKE #{roleId}

        parent_id = #{parentId}

            AND parent_id LIKE #{parentId}

        parent_ids = #{parentIds}

            AND parent_ids LIKE #{parentIds}

        locked = #{locked}

            AND locked LIKE #{locked}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}
