mapper.update:
update resource set
        name = #{name}
               ,
        type = #{type}
               ,
        url = #{url}
               ,
        priority = #{priority}
               ,
        parent_id = #{parentId}
               ,
        parent_ids = #{parentIds}
               ,
        permission = #{permission}
               ,
        available = #{available}
               ,
        created_at = #{createdAt}
               ,
        updated_at = #{updatedAt}
  where id = #{id}

mapper.select:
select * from resource where 1=1
        name = #{name}

            AND name LIKE #{name}

        type = #{type}

            AND type LIKE #{type}

        url = #{url}

            AND url LIKE #{url}

        priority = #{priority}

            AND priority LIKE #{priority}

        parent_id = #{parentId}

            AND parent_id LIKE #{parentId}

        parent_ids = #{parentIds}

            AND parent_ids LIKE #{parentIds}

        permission = #{permission}

            AND permission LIKE #{permission}

        available = #{available}

            AND available LIKE #{available}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}

limit #{pageNo}, #{pageSize}

select count(id) from resource where 1=1
        name = #{name}

            AND name LIKE #{name}

        type = #{type}

            AND type LIKE #{type}

        url = #{url}

            AND url LIKE #{url}

        priority = #{priority}

            AND priority LIKE #{priority}

        parent_id = #{parentId}

            AND parent_id LIKE #{parentId}

        parent_ids = #{parentIds}

            AND parent_ids LIKE #{parentIds}

        permission = #{permission}

            AND permission LIKE #{permission}

        available = #{available}

            AND available LIKE #{available}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}
