mapper.insert:
insert into role(
        name
          ,
        desc
          ,
        created_at
          ,
        updated_at
    ) values(
        #{name}
           ,
        #{desc}
           ,
        #{createdAt}
           ,
        #{updatedAt}
    )

mapper.update:
update role set
        name = #{name}
               ,
        desc = #{desc}
               ,
        created_at = #{createdAt}
               ,
        updated_at = #{updatedAt}
  where id = #{id}

mapper.select:
select * from role where 1=1
        name = #{name}

            AND name LIKE #{name}

        desc = #{desc}

            AND desc LIKE #{desc}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}

limit #{pageNo}, #{pageSize}

select count(id) from role where 1=1
        name = #{name}

            AND name LIKE #{name}

        desc = #{desc}

            AND desc LIKE #{desc}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}
