mapper.update:
update worker set
        name = #{name}
               ,
        username = #{username}
               ,
        password = #{password}
               ,
        gender = #{gender}
               ,
        birth_date = #{birthDate}
               ,
        mobile = #{mobile}
               ,
        native_place = #{nativePlace}
               ,
        dealer_id = #{dealerId}
               ,
        work_type = #{workType}
               ,
        locked = #{locked}
               ,
        created_at = #{createdAt}
               ,
        updated_at = #{updatedAt}
  where id = #{id}

mapper.select:
select * from worker where 1=1
        name = #{name}

            AND name LIKE #{name}

        username = #{username}

            AND username LIKE #{username}

        password = #{password}

            AND password LIKE #{password}

        gender = #{gender}

            AND gender LIKE #{gender}

        birth_date = #{birthDate}

            AND birth_date LIKE #{birthDate}

        mobile = #{mobile}

            AND mobile LIKE #{mobile}

        native_place = #{nativePlace}

            AND native_place LIKE #{nativePlace}

        dealer_id = #{dealerId}

            AND dealer_id LIKE #{dealerId}

        work_type = #{workType}

            AND work_type LIKE #{workType}

        locked = #{locked}

            AND locked LIKE #{locked}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}

limit #{pageNo}, #{pageSize}

select count(id) from worker where 1=1
        name = #{name}

            AND name LIKE #{name}

        username = #{username}

            AND username LIKE #{username}

        password = #{password}

            AND password LIKE #{password}

        gender = #{gender}

            AND gender LIKE #{gender}

        birth_date = #{birthDate}

            AND birth_date LIKE #{birthDate}

        mobile = #{mobile}

            AND mobile LIKE #{mobile}

        native_place = #{nativePlace}

            AND native_place LIKE #{nativePlace}

        dealer_id = #{dealerId}

            AND dealer_id LIKE #{dealerId}

        work_type = #{workType}

            AND work_type LIKE #{workType}

        locked = #{locked}

            AND locked LIKE #{locked}

