mapper.update:
update dealer set
        name = #{name}
               ,
        username = #{username}
               ,
        password = #{password}
               ,
        company_name = #{companyName}
               ,
        mobile = #{mobile}
               ,
        telephone = #{telephone}
               ,
        admin_id = #{adminId}
               ,
        province_code = #{provinceCode}
               ,
        city_code = #{cityCode}
               ,
        area_code = #{areaCode}
               ,
        address = #{address}
               ,
        role_id = #{roleId}
               ,
        locked = #{locked}
               ,
        email = #{email}
               ,
        invite_code = #{inviteCode}
               ,
        created_at = #{createdAt}
               ,
        updated_at = #{updatedAt}
  where id = #{id}

mapper.select:
select * from dealer where 1=1
        name = #{name}

            AND name LIKE #{name}

        username = #{username}

            AND username LIKE #{username}

        password = #{password}

            AND password LIKE #{password}

        company_name = #{companyName}

            AND company_name LIKE #{companyName}

        mobile = #{mobile}

            AND mobile LIKE #{mobile}

        telephone = #{telephone}

            AND telephone LIKE #{telephone}

        admin_id = #{adminId}

            AND admin_id LIKE #{adminId}

        province_code = #{provinceCode}

            AND province_code LIKE #{provinceCode}

        city_code = #{cityCode}

            AND city_code LIKE #{cityCode}

        area_code = #{areaCode}

            AND area_code LIKE #{areaCode}

        address = #{address}

            AND address LIKE #{address}

        role_id = #{roleId}

            AND role_id LIKE #{roleId}

        locked = #{locked}

            AND locked LIKE #{locked}

        email = #{email}

            AND email LIKE #{email}

        invite_code = #{inviteCode}

            AND invite_code LIKE #{inviteCode}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}

limit #{pageNo}, #{pageSize}

select count(id) from dealer where 1=1
        name = #{name}

