mapper.update:
update product set
        name = #{name}
               ,
        specification = #{specification}
               ,
        hardness = #{hardness}
               ,
        texture = #{texture}
               ,
        color = #{color}
               ,
        desc = #{desc}
               ,
        created_at = #{createdAt}
               ,
        updated_at = #{updatedAt}
               ,
        creator = #{creator}
               ,
        price = #{price}
  where id = #{id}

mapper.select:
select * from product where 1=1
        name = #{name}

            AND name LIKE #{name}

        specification = #{specification}

            AND specification LIKE #{specification}

        hardness = #{hardness}

            AND hardness LIKE #{hardness}

        texture = #{texture}

            AND texture LIKE #{texture}

        color = #{color}

            AND color LIKE #{color}

        desc = #{desc}

            AND desc LIKE #{desc}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}

        creator = #{creator}

            AND creator LIKE #{creator}

        price = #{price}

            AND price LIKE #{price}

limit #{pageNo}, #{pageSize}

select count(id) from product where 1=1
        name = #{name}

            AND name LIKE #{name}

        specification = #{specification}

            AND specification LIKE #{specification}

        hardness = #{hardness}

            AND hardness LIKE #{hardness}

        texture = #{texture}

            AND texture LIKE #{texture}

        color = #{color}

            AND color LIKE #{color}

        desc = #{desc}

            AND desc LIKE #{desc}

        created_at = #{createdAt}

            AND created_at LIKE #{createdAt}

        updated_at = #{updatedAt}

            AND updated_at LIKE #{updatedAt}

        creator = #{creator}

            AND creator LIKE #{creator}

        price = #{price}

            AND price LIKE #{price}
